#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <notchFilter/iirNotch.h>
// pydoc.h is automatically generated in the build directory
#include <iirNotch_pydoc.h>

void bind_iirNotch(py::module& m)
{

    using iirNotch    = ::gr::notchFilter::iirNotch;


    py::class_<iirNotch, gr::sync_block, gr::block, gr::basic_block,
        std::shared_ptr<iirNotch>>(m, "iirNotch", D(iirNotch))

        .def(py::init(&iirNotch::make),
           py::arg("sampRate"),
           py::arg("targetFreq"),
           py::arg("width"),
           D(iirNotch,make)
        )
        




        
        .def("set_wc",&iirNotch::set_wc,       
            py::arg("wc"),
            D(iirNotch,set_wc)
        )


        
        .def("set_wb",&iirNotch::set_wb,       
            py::arg("wb"),
            D(iirNotch,set_wb)
        )

        ;




}








